declare module '*.scss' {
  const src: { [className: string]: string };
  export = src;
}

declare module '*.svg' {
  const src: any;
  export default src;
}

declare module '*.ttf' {
  const src: any;
  export default src;
}

declare module '*.png' {
  const src: any;
  export default src;
}

declare module '*.jpg' {
  const src: any;
  export default src;
}

declare module '*.webm' {
  const src: any;
  export default src;
}
